$1_12_4(function($) {
	var $w = $(window);

	// メインビジュアル切り替え
	(function() {
		var $mv = $('#js-mv');
		var mv_style = $mv.attr('style');
		var mv_img = mv_style.match(/background-image: ?url\((.*)\)/)[1];
		var mv_img_sp = $mv.data('bg-sp');

		$w.on({
			'_size.notSp': function() {
				$mv.attr('style', 'background-image: url(' + mv_img + ')');
			},
			'_size.sp': function() {
				$mv.attr('style', 'background-image: url(' + mv_img_sp + ')');
			},
		});
	})();

	// 追従ナビゲーション
	(function() {
		var $anchor_sect = $('#admission, #learning, #career, #campus');
		var anchor_sect_length = $anchor_sect.length;
		var $nav_menu = $('#js-nav_menu');
		var controller = new ScrollMagic.Controller();
		var scene_follow;
		var arr_scene_anchor = [];

		function fnPC() {
			var nav_menu_h = $nav_menu.height();

			$nav_menu.height(nav_menu_h);

			scene_follow = new ScrollMagic.Scene({
				duration: ($('#ct_area').height() - 945),
				offset: ($nav_menu.height() / 2),
				triggerElement: '#js-nav_menu',
			}).setPin('#js-nav_menu').addTo(controller);

			for (var i = 0; i < anchor_sect_length; i++) {
				var sect_id = $($anchor_sect[i]).attr('id');

				arr_scene_anchor[i] = new ScrollMagic.Scene({
					duration: $('#' + sect_id).height(),
					triggerElement: '#' + sect_id,
				}).setClassToggle('#anchor_' + sect_id, 'is-crnt').addTo(controller);
			}
		}
		function fnSP() {
			if (scene_follow !== undefined) {
				scene_follow.destroy(true);
			}
			$nav_menu.height('auto');

			if (arr_scene_anchor.length !== 0) {
				for (var i = 0; i < anchor_sect_length; i++) {
					arr_scene_anchor[i].destroy(true);
				}
				arr_scene_anchor = [];
			}
		}

		$w.on({
			'_size.notSp': function() {
				fnPC();
			},
			'_size.sp': function() {
				fnSP();
			},
		});
	})();

	// トピックススライド
	(function() {
		var $topics_slide = $('#js-topics_slide');

		$topics_slide.slick({
			autoplay: true,
			arrows: false,
			dots: true,
			fade: true,
			pauseOnFocus: false,
			speed: 700,
		});
	})();

	function shuffleArr(arr) {
		var n = arr.length, t, i;
		while (n) {
			i = Math.floor(Math.random() * n--);
			t = arr[n];
			arr[n] = arr[i];
			arr[i] = t;
		}
		return arr;
	}

	// キャンパスライフの背景入れ替え
	(function() {
		var $bg_list = $('#js-bg_list');
		var $bg_list_li = $bg_list.find('li');

		$bg_list.html(shuffleArr($bg_list_li));
	})();

	// レスポンシブ対応用イベントトリガー
	(function() {
		var size_base;
		var bp = 640;

		$(function() {
			size_base = (window.innerWidth <= bp) ? 'sp' : 'notSp';

			if (size_base === 'sp') {
				$w.trigger('_size.sp');
			} else {
				$w.trigger('_size.notSp');
			}
		});
		$w.on('resize', function() {
			var size = (window.innerWidth <= bp) ? 'sp' : 'notSp';

			if (size !== size_base) {
				if (size === 'sp') {
					$w.trigger('_size.sp');
				} else {
					$w.trigger('_size.notSp');
				}
				size_base = size;
			}
		});
	})();
});


